<?phpclass MailSender {    function __construct($toMail, $fromMail, $fromName, $subject, $body) {        require 'system/libs/PHPMailer/PHPMailerAutoload.php';        $mail = new PHPMailer(); // create a new object        $mail->IsSMTP(); // enable SMTP        $mail->SMTPDebug = 1; // debugging: 1 = errors and messages, 2 = messages only        $mail->SMTPAuth = true; // authentication enabled	$mail->SMTPSecure = 'tls';		        $mail->Host = MAIL_HOST;        $mail->Port = MAIL_PORT;        $mail->CharSet = MAIL_CHARSET;                $mail->IsHTML(true);        $mail->Username = MAIL_ADDRESS;        $mail->Password = MAIL_ADDRESS_PASS;        $mail->SetFrom($fromMail, $fromName);        $mail->Subject = $subject;        $mail->Body = $body;        $mail->AddAddress($toMail);        if (!$mail->Send()) {            echo "Mailer Error: " . $mail->ErrorInfo;			exit;        } else {            echo "Message has been sent";        }    }}